import React, {useState} from "react";
import { View, StyleSheet, Text, TouchableOpacity, Alert, ScrollView } from "react-native";
import { FontAwesomeIcon } from "@fortawesome/react-native-fontawesome";
import AsyncStorage from "@react-native-async-storage/async-storage";

import {
  faEdit,
  faCopy,
  faSignOutAlt,
  faUserAlt,
  faInfo,
  faHome,
  faCaretRight,
  faCaretLeft,
  faBookOpen,
} from "@fortawesome/free-solid-svg-icons";
import { print } from "./print";


const resetData = async (value) => {
  try {
    await AsyncStorage.setItem("staffPicked", "")
    return value;
  } catch (e) {
    console.log(e)
    alert("Error saving, try again");
  }
};


export const Widgets = (props) => {
  const [page, setPage] = useState(1)

  const handlePrint = () => {
    if (props.info.dateof !== "none") {
      print(props.info).then(res=>console.log(res)).catch(err=>console.log(err))
    } else {
      alert("Nothing to print yet");
    }
  };

  return (
    <View style={styles.container}>
      <View style={styles.caratContainer}>      

      { page === 2 && 
<TouchableOpacity onPress={()=>setPage(1)}>
 <FontAwesomeIcon
 style={{ alignSelf: "center" }}
 icon={faCaretLeft}
 color="#69a3ff"
  size={32}
  />
  </TouchableOpacity>  
} 
</View>
    <View style={styles.widgetContainer}>

      { 
      (page === 1) ? 
      <>
      <View style={styles.widget}>
        <TouchableOpacity
          onPress={() =>
            props.staffOnDuty.length
              ? props.navigate("UpdateInfo", {
                  updateInfo: props.setInfo,
                  prevInfo: props.info,
                  staffOnDuty: props.staffOnDuty,
                })
              : alert("Someone must be on duty")
          }
        >
          <Text style={styles.text}>Update children count</Text>
          <FontAwesomeIcon
            style={{ alignSelf: "center" }}
            icon={faEdit}
            size={32}
          />
        </TouchableOpacity>
      </View>
      <View style={styles.widget}>
        <TouchableOpacity
          onPress={() => props.navigate("ChangeTeacher", {
                  staffOnDuty: props.staffOnDuty,
                  setStaffOnDuty: props.setStaffOnDuty,
                })
          }
        >
          <Text style={styles.text}>Update staff on duty</Text>
          <FontAwesomeIcon
            style={{ alignSelf: "center" }}
            icon={faUserAlt}
            size={32}
          />
        </TouchableOpacity>
      </View>
      <View style={styles.widget}>
        <TouchableOpacity
          onPress={() =>
            props.info.dateof !== "none"
              ? props.navigate("PrintSheet")
              : alert("Log some information first")
          }
        >
          <Text style={styles.text}>View ratio sheet</Text>
          <FontAwesomeIcon
            style={{ alignSelf: "center" }}
            icon={faCopy}
            size={32}
          />
        </TouchableOpacity>
      </View>
      <View style={styles.widget}>
        <TouchableOpacity
          onPress={() =>
 props.navigate("StaffDirectory", {
                  staffOnDuty: props.staffOnDuty,
                })
          }
        >
          <Text style={styles.text}>Update staff directory</Text>
          <FontAwesomeIcon
            style={{ alignSelf: "center" }}
            icon={faInfo}
            size={32}
          />
        </TouchableOpacity>
      </View>
     </> : 
      <>

      <View style={styles.widget}>
        <TouchableOpacity
          onPress={() =>
            props.info.dateof !== "none"
              ? props.navigate("DiplomaInfo", {
                  staffOnDuty: props.staffOnDuty,
                })
              : alert(props.info.date)
          }
        >
          <Text style={styles.text}>View weekly data</Text>
          <FontAwesomeIcon
            style={{ alignSelf: "center" }}
            icon={faBookOpen}
            size={32}
          />
        </TouchableOpacity>
      </View>

      <View style={styles.widget}>
        <TouchableOpacity
          disabled={props.info.length === 1}
          onPress={resetData}
        >
          <Text style={styles.text}>Reset staff on duty</Text>
          <FontAwesomeIcon
            style={{ alignSelf: "center" }}
            icon={faHome}
            size={32}
          />
        </TouchableOpacity>
      </View>
      </>
}

</View>
<View style={styles.caratContainer}>      
{ page === 1 && 
<TouchableOpacity onPress={()=>setPage(2)}>
 <FontAwesomeIcon
 style={{ alignSelf: "center" }}
 icon={faCaretRight}
 color="#69a3ff"
  size={32}
  />
  </TouchableOpacity>  
} 
</View>
    </View>

  );
};

const styles = StyleSheet.create({
  container: {
    flex: 0.4,
    flexDirection: "row",
    justifyContent: "space-between",
  },
  caratContainer: {
    flex: 0.05,
    alignItems: "center",
    justifyContent: "space-around",
  },

  widgetContainer: {
    flex: 0.95,
    flexDirection: "row",
    justifyContent: "space-evenly",
    flexWrap: "wrap",
    alignItems: "center",
    paddingLeft: "0%"
  },
  text: {
    fontFamily: "mainFont",
    fontSize: 25,
    marginBottom: "5%",
  },
  stat: {
    flex: 1,
    flexDirection: "column",
    fontFamily: "mainFont",
  },
  widget: {
    height: "35%",
    width: "40%",
    padding: "2.5%",
    alignItems: "center",
    justifyContent: "space-evenly",
    textAlign: "center",
    backgroundColor: "white",
    borderRadius: 15,
    shadowColor: "#000",
    shadowOffset: {
      width: 0,
      height: 5,
    },
    marginBottom: "5%",
    shadowOpacity: 0.5,
    shadowRadius: 5.0,
    paddingLeft: "2%",
    elevation: 10,
  },
});
